mapper.select:
select e.employeeNumber as empNo, e.name as name, (select DTL_C_NM from CMCDDTLM c1 where c1.GRP_C='position' and c1.DTL_C=position) as position, e.division as dcode, e.position as pcode, e.basic_dept, (select dept_nm from dept where dept_no=e.basic_dept) basic_dept_nm, (select DTL_C_NM from CMCDDTLM c2 where c2.GRP_C='organization' and c2.DTL_C in(select org_no from dept where dept_no=e.basic_dept)) as basic_dept_org_nm, e.first_dept, ifnull((select dept_nm from dept where dept_no=e.first_dept),"") first_dept_nm, ifnull((select DTL_C_NM from CMCDDTLM c3 where c3.GRP_C='organization' and c3.DTL_C in(select org_no from dept where dept_no=e.first_dept)),"") as first_dept_org_nm, e.second_dept, e.three_dept, e.four_dept, e.gender, e.country, e.joinDate, e.status, e.manager, (select auth from manager m where m.empNo=e.employeeNumber) as auth, e.email, e.office, e.seat, e.profile_name, e.office_number, (SELECT DTL_C_NM from CMCDDTLM c where c.GRP_c='color' and c.DTL_C = (SELECT color from dept where dept_no=e.division)) color
from employee e
${keyword} like concat('%',#{search},'%')
and (first_dept=#{search} or second_dept=#{search} or three_dept=#{search} or four_dept=#{search})
${keyword}=#{search}
order by ${sortBy} ${direction}
order by joindate asc
limit ${page},${limit}

select name,email,employeeNumber empNo,division dcode,position pcode, (select DTL_C_NM from CMCDDTLM c1 where c1.GRP_C='position' and c1.DTL_C=position) as position, (SELECT CONCAT(dept_nm,' ', DTL_C_NM) AS dept_nm FROM dept d, CMCDDTLM c where c.GRP_C='organization' and c.DTL_C = d.org_no and dept_mgr_no=2 and d.dept_no = division) division from employee
name like concat('%',#{mgr},'%')
and division=#{division}
